mapper.update:
update poetry_table
set
poetry_id = #{poetry_id}
,name=#{name}
,author = #{author}
,content = #{content}
,content = #{content}
,addition_info = #{addition_info}
,class_info = #{class_info}
,class_info_explain=#{class_info_explain}
,main_class=#{main_class}
,main_class_explain=#{main_class_explain}
,source=#{source}
,source_explain=#{source_explain}
,transfer_info=#{transfer_info}
,background_info=#{background_info}
,analyses_info=#{analyses_info}
,likes=#{likes}
where poetry_id=#{poetry_id}

mapper.select:
select * from like_poetry_table
where user_id=#{user_id} and poetry_id=#{poetry_id}
limit 1

mapper.insert:
insert into like_poetry_table(user_id, poetry_id, create_time)
values (
#{user_id,jdbcType=INTEGER},
#{poetry_id,jdbcType=INTEGER},
#{create_time,jdbcType=CHAR}
)

mapper.delete:
delete from like_poetry_table
where
user_id=#{user_id} and poetry_id=#{poetry_id}

mapper.select:
select poetry_id,name,author,content,likes,bt.class_info,pt.class_info,bt.image_base_url,bt.origin_url,class_info_explain,main_class,main_class_explain,source,source_explain,text_color from poetry_table pt left join bgImage_table bt on pt.class_info = bt.class_info
pt.name like CONCAT('%',#{keyword},'%') or pt.author like CONCAT('%',#{keyword},'%') or pt.content like CONCAT('%',#{keyword},'%')
order by pt.poetry_id desc limit #{from_index},#{count}

select name,author,content from
poetry_table
where poetry_id >= #{poetry_id}
order by poetry_id asc
limit #{from_index},#{count}
