mapper.update:
update user_table
set
user_id = #{user_id}
,nick_name=#{nick_name}
,password = #{password}
,user_poetry_class = #{user_poetry_class}
,poetry_storage = #{poetry_storage}
,email = #{email}
,last_login_time = #{last_login_time}
,last_loginout_time=#{last_loginout_time}
where user_id=#{user_id}

mapper.select:
select * from user_table

     nick_name = #{nick_name}
    and user_id=#{user_id}
